import {
  View,
  Text,
  Image,
  StyleSheet,
  TouchableOpacity,
  FlatList,
  Modal,
} from 'react-native';
import React, {useState} from 'react';

export default function FullPill({navigation, timing}: {navigation: any, timing: string}) {
  const [modalVisible, setModalVisible] = useState(false);

  const [medicineList, changeMedicineList] = useState([
    {
      idx: '1',
      name: '타이레놀',
      eaten: true,
      image:
        'data:image/jpeg;base64,',
      time: '7:57:22',
    },
    {
      idx: '2',
      name: '비타민A',
      eaten: true,
      image:
        'data:image/jpeg;base64,',
      time: '7:57:22',
    },
    {
      idx: '3',
      name: '비타민B',
      eaten: false,
      image: '',
      time: '',
    },
    {
      idx: '4',
      name: '비타민C',
      eaten: false,
      image: '',
      time: '',
    },
    {
      idx: '5',
      name: '아르기닌',
      eaten: true,
      image:
        'data:image/jpeg;base64,',
      time: '',
    },
  ]);

  return (
    <View>
      <TouchableOpacity
        onPress={() => {
          setModalVisible(true);
        }}>
        <View style={styles.eatMedicineContainer}>
          <Image
            style={styles.pillImage}
            source={require('../assets/HealthCareIcon/FullPill.png')}
          />
        </View>
      </TouchableOpacity>

      {/* Modal to show the list of eaten medicines */}
      <Modal animationType="slide" transparent={true} visible={modalVisible}>
        <View style={styles.EatMedicineListContainer}>
          <Text style={styles.titleText}>{timing}</Text>
          <FlatList
            keyExtractor={item => item.idx}
            data={medicineList}
            renderItem={({item}) => (
              <EatenMedicineInform
                medicineName={item.name}
                isEaten={item.eaten}
                imageSource={item.image}
                eatTime={item.time}
              />
            )}
            numColumns={2}
          />
          <TouchableOpacity
            style={styles.modalCloseButton}
            onPress={() => setModalVisible(false)}>
            <Text style={styles.modalCloseText}>X</Text>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
}

function EatenMedicineInform({
  medicineName,
  isEaten,
  imageSource,
  eatTime,
}: {
  medicineName: string;
  isEaten: boolean;
  imageSource: string;
  eatTime: string;
}) {

  return (
    <View style={styles.EatenMedicineContainer}>
      <Text style={styles.EatenMedicineTitle}>{medicineName}</Text>

      {/* // image style from styles.EatenMedicineImage and source from imageSource */}
      <Image
        style={styles.EatenMedicineImage}
        // source={{ uri: imageSource }}
        source={isEaten? {uri: imageSource} : require('../assets/HealthCareIcon/AddCameraIcon.png')}
      />

      <Text style={styles.EatenMedicineTime}>{isEaten ? eatTime : '아직 복용하지\n않았습니다.'}</Text>
    </View>
  );
}

